script:
frappe.ui.form.on('Subscription', {
  refresh(frm) {
    if (!frm.doc.custom_is_provisioned && frm.doc.docstatus===0) {
      frm.add_custom_button('Provision Site', () => {
        frappe.call({
          method: 'erp_saas.erp_saas.api.provisioning.provision_site_remote',
          args: { subscription_name: frm.doc.name },
          freeze: false,
          // this callback receives (percent, message)
          progress: (percent, message) => {
            frappe.show_progress(message, percent);
          },
          callback: (r) => {
            if (r.message) {
              frappe.msgprint(r.message);
            }
            frm.reload_doc();
          }
        });
      });
    }
  }
});


