frappe.ui.form.on('Subscription', {
  refresh(frm) {
    // only when the Subscription is saved and has a plan/site
    if (!frm.is_new() && frm.doc.custom_site_name) {
      frm.add_custom_button(__('Upgrade Plan'), () => {
        frappe.call({
          method: 'erp_saas.erp_saas.api.site_config_update.upgrade_plan_quota',
          args: { subscription_name: frm.doc.name },
          freeze: true,
          freeze_message: __('Upgrading plan quotasâ€¦'),
          callback: r => {
            if (r.message) {
              frappe.msgprint(r.message);
            }
          }
        });
      });
    }
  }
});
